import axios from 'axios';
import { REGISTER_SUCCESS, REGISTER_FAILURE, USER_LOADED, AUTH_ERROR, LOGIN_SUCCESS, LOGIN_FAILURE } from './types';
import { setAlert } from './alert';
import setAuthToken from '../utils/setAuthToken';


//Load User
export const loadUser = () => async dispatch => {

    if (localStorage.token) {
        console.log("inside localstorage");
        setAuthToken(localStorage.token);
        console.log("done with local storage")
    }

    try {

        if (localStorage.token) {
            axios.defaults.baseURL = 'http://localhost:5000';
            const res = await axios.get('/api/auth');

            dispatch({
                type: USER_LOADED,
                payload: res.data
            });
            console.log("finished the try block");

        }


    } catch (err) {
        console.log("inside the error block of actoins/auth.js")
        dispatch({
            type: AUTH_ERROR
        });

    }


}

//Register user 
export const register = ({ name, email, password }) => async dispatch => {


    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({ name, email, password });
    try {
        const res = await axios.post('/api/users', body, config);
        dispatch({
            type: REGISTER_SUCCESS,
            payload: res.data
        });
        dispatch(loadUser());

    }
    catch (err) {
        const errors = err.response.data.errors;
        if (errors) {
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }
        dispatch({
            type: REGISTER_FAILURE
        });


    }
}

//Login user 
export const login = (email, password) => async dispatch => {


    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({ email, password });
    try {
        const res = await axios.post('/api/users/login', body, config);
        dispatch({
            type: LOGIN_SUCCESS,
            payload: res.data
        });
        dispatch(loadUser());

    }
    catch (err) {
        const errors = err.response.data.errors;
        if (errors) {
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }
        dispatch({
            type: LOGIN_FAILURE
        });

    }
}
